import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './style.css';

import starIcon from './../../img/icons/star.svg';
import likeIcon from './../../img/icons/like.svg';
import likeFilledIcon from './../../img/icons/like-filled.svg';

const BookDetail = () => {
  const { id } = useParams(); // получаем ID книги из URL
  const [book, setBook] = useState(null);
  const [liked, setLiked] = useState(false);

  // Пример: если ты используешь OpenLibrary API
  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await fetch(`https://openlibrary.org/works/${id}.json`);
        const data = await response.json();
        setBook({
          title: data.title,
          description:
            typeof data.description === 'string'
              ? data.description
              : data.description?.value || 'No description available.',
          author: data.authors ? data.authors[0]?.name || 'Unknown Author' : 'Unknown',
          cover: data.covers
            ? `https://covers.openlibrary.org/b/id/${data.covers[0]}-L.jpg`
            : 'https://via.placeholder.com/200x300?text=No+Cover',
          rating: 4.5,
          price: (Math.random() * 20 + 10).toFixed(2),
        });
      } catch (error) {
        console.error('Error fetching book:', error);
      }
    };
    fetchBook();
  }, [id]);

  // Проверка лайка
  useEffect(() => {
    if (book) {
      const saved = JSON.parse(localStorage.getItem('favorites')) || [];
      const isLiked = saved.some((b) => b.title === book.title);
      setLiked(isLiked);
    }
  }, [book]);

  // Добавить / удалить из избранного
  const toggleLike = () => {
    const saved = JSON.parse(localStorage.getItem('favorites')) || [];
    let updated;

    if (liked) {
      updated = saved.filter((b) => b.title !== book.title);
    } else {
      const exists = saved.some((b) => b.title === book.title);
      if (!exists) {
        updated = [
          ...saved,
          {
            title: book.title,
            author: book.author,
            image: book.cover,
            rating: book.rating,
            price: book.price,
          },
        ];
      } else updated = saved;
    }

    localStorage.setItem('favorites', JSON.stringify(updated));
    setLiked(!liked);
    window.dispatchEvent(new Event('favoritesUpdated'));
  };

  if (!book) return <p className="loading">Loading book details...</p>;

  return (
    <main className="book-detail-page">
      <div className="book-detail-card">
        <img src={book.cover} alt={book.title} className="book-detail-image" />

        <div className="book-detail-info">
          <h1 className="book-detail-title">{book.title}</h1>
          <p className="book-detail-author">by {book.author}</p>

          <div className="book-detail-rating">
            {Array.from({ length: 5 }, (_, i) => (
              <img key={i} src={starIcon} alt="star" className="star" />
            ))}
            <span>{book.rating} / 5</span>
          </div>

          <p className="book-detail-price">${book.price}</p>

          <p className="book-detail-description">{book.description}</p>

          <div className="book-detail-actions">
            <button className="btn">Add to bag</button>
            <img
              src={liked ? likeFilledIcon : likeIcon}
              alt="like"
              className="book-like"
              onClick={toggleLike}
            />
          </div>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
