import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './style.css';

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBook = async () => {
      setLoading(true);
      try {
        const res = await fetch(`https://openlibrary.org/search.json?title=${encodeURIComponent(title)}`);
        const data = await res.json();
        if (data.docs && data.docs.length > 0) {
          const b = data.docs[0];
          setBook({
            title: b.title,
            author: b.author_name ? b.author_name.join(', ') : 'Unknown',
            cover: b.cover_i ? `https://covers.openlibrary.org/b/id/${b.cover_i}-L.jpg` : null,
            first_publish_year: b.first_publish_year,
            price: Math.floor(Math.random() * 30) + 10,
            rating: Math.floor(Math.random() * 5) + 1,
          });
        } else {
          setBook(null);
        }
      } catch (error) {
        console.error('Error fetching book:', error);
        setBook(null);
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [title]);

  const addToFavorites = () => {
    const saved = JSON.parse(localStorage.getItem('favorites')) || [];
    const exists = saved.some((item) => item.title === book.title);

    if (!exists) {
      const updated = [...saved, { 
        title: book.title, 
        author: book.author, 
        image: book.cover, // ✅ теперь добавляем обложку
        price: book.price, 
        rating: book.rating 
      }];
      localStorage.setItem('favorites', JSON.stringify(updated));
      window.dispatchEvent(new Event('favoritesUpdated'));
      alert('Added to favorites 💛');
    }
  };

  const addToCart = () => {
    const saved = JSON.parse(localStorage.getItem('cart')) || [];
    const exists = saved.some((item) => item.title === book.title);

    if (!exists) {
      const updated = [...saved, { 
        title: book.title, 
        author: book.author, 
        image: book.cover, // ✅ обложка теперь сохраняется
        price: book.price, 
        rating: book.rating 
      }];
      localStorage.setItem('cart', JSON.stringify(updated));
      window.dispatchEvent(new Event('cartUpdated'));
      alert('Added to bag 🛍️');
    }
  };

  if (loading) return <p>Loading...</p>;

  if (!book) {
    return (
      <div className="book-detail">
        <p>Book not found</p>
        <button className="btn" onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  return (
    <main className="book-detail">
      <button className="btn back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="book-detail__content">
        {book.cover && <img src={book.cover} alt={book.title} className="book-detail__image" />}

        <div className="book-detail__info">
          <h1>{book.title}</h1>
          <p className="author">by {book.author}</p>
          {book.first_publish_year && <p className="year">First published: {book.first_publish_year}</p>}
          <p className="desc">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
            This is a placeholder description for the book detail page.
          </p>
          <div className="book-detail__buttons">
            <button className="btn primary" onClick={addToCart}>Add to Bag</button>
            <button className="btn secondary" onClick={addToFavorites}>Add to Favorites</button>
          </div>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
