import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useBooks } from "../../context/BooksContext";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

import starIcon from "../../img/icons/star.svg";
import likeIcon from "../../img/icons/like.svg";
import likeFilledIcon from "../../img/icons/like-filled.svg";

const BookDetail = () => {
  const { title } = useParams();
  const { books } = useBooks();

  const [book, setBook] = useState(null);
  const [liked, setLiked] = useState(false);
  const [inCart, setInCart] = useState(false);

  useEffect(() => {
    if (!books?.length) return;

    const found = books.find((b) => b.title === title);
    if (!found) return;

    // Готовим объект с нужными полями
    const formattedBook = {
      title: found.title,
      author:
        Array.isArray(found.author_name) && found.author_name.length > 0
          ? found.author_name.join(", ")
          : "Unknown Author",
      price: found.price || Math.floor(Math.random() * 30) + 10,
      image: found.cover_i
        ? `https://covers.openlibrary.org/b/id/${found.cover_i}-L.jpg`
        : "https://via.placeholder.com/300x450?text=No+Cover",
      rating: found.rating || 4,
    };

    setBook(formattedBook);

    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const cart = JSON.parse(localStorage.getItem("cart")) || [];

    setLiked(favorites.some((f) => f.title === formattedBook.title));
    setInCart(cart.some((c) => c.title === formattedBook.title));
  }, [title, books]);

  const toggleFavorite = () => {
    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
    let updated;

    if (liked) {
      updated = favorites.filter((b) => b.title !== book.title);
    } else {
      updated = [...favorites, book];
    }

    localStorage.setItem("favorites", JSON.stringify(updated));
    setLiked(!liked);
    window.dispatchEvent(new Event("favoritesUpdated"));
  };

  const toggleCart = () => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    let updated;

    if (inCart) {
      updated = cart.filter((b) => b.title !== book.title);
    } else {
      updated = [...cart, book];
    }

    localStorage.setItem("cart", JSON.stringify(updated));
    setInCart(!inCart);
    window.dispatchEvent(new Event("cartUpdated"));
  };

  if (!book) {
    return (
      <main className="book-detail">
        <p className="book-detail__empty">Book not found</p>
      </main>
    );
  }

  return (
    <main className="book-detail">
      <div className="book-detail__wrapper">
        <img
          src={book.image}
          alt={book.title}
          className="book-detail__image"
        />

        <div className="book-detail__info">
          <h2 className="book-detail__title">{book.title}</h2>
          <p className="book-detail__author">{book.author}</p>

          <div className="book-detail__rating">
            {Array.from({ length: 5 }).map((_, i) => (
              <img
                key={i}
                src={starIcon}
                alt="star"
                className={`book-detail__star ${
                  i < book.rating ? "filled" : ""
                }`}
              />
            ))}
          </div>

          <p className="book-detail__price">${book.price}</p>

          <p className="book-detail__desc">
            Discover the story of <strong>{book.title}</strong> — a captivating
            book that will stay with you long after you finish reading.
          </p>

          <div className="book-detail__actions">
            <button
              className={`book-detail__btn ${inCart ? "in-cart" : ""}`}
              onClick={toggleCart}
            >
              {inCart ? "Added to bag" : "Add to bag"}
            </button>

            <img
              src={liked ? likeFilledIcon : likeIcon}
              alt="like"
              className="book-detail__like"
              onClick={toggleFavorite}
            />
          </div>
        </div>
      </div>

      {books.length > 1 && (
        <section className="related-books">
          <h3 className="related-books__title">You also may like</h3>
          <div className="books-grid">
            {books
              .filter((b) => b.title !== book.title)
              .slice(0, 5)
              .map((b, i) => (
                <BookCard
                  key={i}
                  image={
                    b.cover_i
                      ? `https://covers.openlibrary.org/b/id/${b.cover_i}-M.jpg`
                      : "https://via.placeholder.com/150x220?text=No+Cover"
                  }
                  rating={b.rating || 4}
                  title={b.title}
                  author={
                    Array.isArray(b.author_name) && b.author_name.length > 0
                      ? b.author_name[0]
                      : "Unknown Author"
                  }
                  price={b.price || Math.floor(Math.random() * 30) + 10}
                />
              ))}
          </div>
        </section>
      )}
    </main>
  );
};

export default BookDetail;
