import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import BookCard from '../../components/bookcard/BookCard';
import './style.css';

import starIcon from './../../img/icons/star.svg';
import likeIcon from './../../img/icons/like.svg';
import likeFilledIcon from './../../img/icons/like-filled.svg';

const BookDetail = () => {
  const { title } = useParams();

  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [liked, setLiked] = useState(false);
  const [inCart, setInCart] = useState(false);
  const [relatedBooks, setRelatedBooks] = useState([]);

  // Загрузка основной книги
  useEffect(() => {
    const fetchBook = async () => {
      setLoading(true);
      try {
        const res = await fetch(`https://openlibrary.org/search.json?title=${encodeURIComponent(title)}`);
        const data = await res.json();

        if (data.docs && data.docs.length > 0) {
          const b = data.docs[0];
          const price = Math.floor(Math.random() * 40) + 10;
          const rating = Math.floor(Math.random() * 5) + 1;

          const currentBook = {
            title: b.title,
            author: b.author_name ? b.author_name.join(', ') : 'Unknown Author',
            cover: b.cover_i
              ? `https://covers.openlibrary.org/b/id/${b.cover_i}-L.jpg`
              : 'https://via.placeholder.com/150x220?text=No+Cover',
            price,
            rating,
            year: b.first_publish_year || '—',
          };

          setBook(currentBook);

          // Проверяем localStorage
          const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
          const cart = JSON.parse(localStorage.getItem('cart')) || [];

          setLiked(favorites.some((f) => f.title === b.title));
          setInCart(cart.some((c) => c.title === b.title));
        } else {
          setBook(null);
        }
      } catch (error) {
        console.error('Error fetching book:', error);
        setBook(null);
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [title]);

  // Загружаем рандомные книги для "You also may like"
  useEffect(() => {
    const fetchRandomBooks = async () => {
      try {
        const res = await fetch(`https://openlibrary.org/search.json?q=random`);
        const data = await res.json();
        if (data.docs) {
          setRelatedBooks(data.docs.slice(0, 6));
        }
      } catch (error) {
        console.error('Error fetching random books:', error);
      }
    };

    fetchRandomBooks();
  }, []);

  // Добавить / удалить из избранного
  const handleAddToFavorites = () => {
    if (!book) return;

    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    let updated;

    if (liked) {
      updated = favorites.filter((b) => b.title !== book.title);
      setLiked(false);
    } else {
      updated = [...favorites, book];
      setLiked(true);
    }

    localStorage.setItem('favorites', JSON.stringify(updated));
    window.dispatchEvent(new Event('favoritesUpdated'));
  };

  // Добавить / удалить из корзины
  const handleAddToCart = () => {
    if (!book) return;

    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    let updated;

    if (inCart) {
      updated = cart.filter((b) => b.title !== book.title);
      setInCart(false);
    } else {
      updated = [...cart, book];
      setInCart(true);
    }

    localStorage.setItem('cart', JSON.stringify(updated));
    window.dispatchEvent(new Event('cartUpdated'));
  };

  // Загрузка
  if (loading)
    return (
      <main className="book-detail">
        <div className="loader"></div>
      </main>
    );

  // Если книга не найдена
  if (!book)
    return (
      <div className="book-detail__empty">
        <p>Book not found</p>
      </div>
    );

  // Отрисовка звёзд
  const stars = Array.from({ length: 5 }, (_, index) => (
    <img
      key={index}
      src={starIcon}
      alt="star"
      className={`book-detail__star ${index < book.rating ? 'filled' : ''}`}
    />
  ));

  return (
    <main className="book-detail">
      <div className="book-detail__wrapper">
        {book.cover && <img src={book.cover} alt={book.title} className="book-detail__image" />}

        <div className="book-detail__info">
          <h2 className="book-detail__title">{book.title}</h2>
          <p className="book-detail__author">{book.author}</p>

          <div className="book-detail__rating">{stars}</div>

          <p className="book-detail__price">${book.price}</p>

          <p className="book-detail__desc">
            Discover the story of <strong>{book.title}</strong> — a captivating book that will stay
            with you long after you finish reading.
          </p>

          <div className="book-detail__actions">
            <button className={`book-detail__btn ${inCart ? 'in-cart' : ''}`} onClick={handleAddToCart}>
              {inCart ? 'Added to bag' : 'Add to bag'}
            </button>

            <img
              src={liked ? likeFilledIcon : likeIcon}
              alt="like"
              className="book-detail__like"
              onClick={handleAddToFavorites}
            />
          </div>
        </div>
      </div>

      {/* You also may like */}
      {relatedBooks.length > 0 && (
        <section className="related-books">
          <h3 className="related-books__title">You also may like</h3>
          <div className="books-grid">
            {relatedBooks.map((b, i) => (
              <BookCard
                key={i}
                image={
                  b.cover_i
                    ? `https://covers.openlibrary.org/b/id/${b.cover_i}-M.jpg`
                    : 'https://via.placeholder.com/150x220?text=No+Cover'
                }
                rating={Math.floor(Math.random() * 5) + 1}
                title={b.title}
                author={b.author_name ? b.author_name[0] : 'Unknown Author'}
                price={Math.floor(Math.random() * 30) + 10}
              />
            ))}
          </div>
        </section>
      )}
    </main>
  );
};

export default BookDetail;
