import React, { useEffect, useState } from 'react';
import './style.css';

const Cart = () => {
  const [cart, setCart] = useState([]);

  // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñƒ Ð¿Ñ€Ð¸ Ð¾Ñ‚ÐºÑ€Ñ‹Ñ‚Ð¸Ð¸ ÑÑ‚Ñ€Ð°Ð½Ð¸Ñ†Ñ‹
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(saved);

    const handleUpdate = () => {
      const updated = JSON.parse(localStorage.getItem('cart')) || [];
      setCart(updated);
    };

    window.addEventListener('cartUpdated', handleUpdate);
    return () => window.removeEventListener('cartUpdated', handleUpdate);
  }, []);

  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ ÐºÐ½Ð¸Ð³Ð¸
  const removeFromCart = (title) => {
    const updated = cart.filter((book) => book.title !== title);
    localStorage.setItem('cart', JSON.stringify(updated));
    setCart(updated);
  };

  // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° Ð²ÑÐµÐ¹ ÐºÐ¾Ñ€Ð·Ð¸Ð½Ñ‹
  const clearCart = () => {
    localStorage.removeItem('cart');
    setCart([]);
  };

  return (
    <main className="cart-page">
      <h1 className="cart-title">ðŸ›’ My Bag</h1>

      {cart.length === 0 ? (
        <p className="cart-empty">Your bag is empty.</p>
      ) : (
        <>
          <div className="cart-grid">
            {cart.map((book, index) => (
              <div key={index} className="cart-item">
                <img src={book.image} alt={book.title} className="cart-image" />
                <div className="cart-info">
                  <h3>{book.title}</h3>
                  <p>{book.author}</p>
                  <p className="cart-price">${book.price}</p>
                </div>
                <button
                  className="btn remove"
                  onClick={() => removeFromCart(book.title)}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>

          <button className="btn clear-all" onClick={clearCart}>
            Clear All
          </button>
        </>
      )}
    </main>
  );
};

export default Cart;
