import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./—ã–µ.css";

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫–Ω–∏–≥–∏ (–∏–∑ localStorage –∏–ª–∏ API)
  useEffect(() => {
    const books = JSON.parse(localStorage.getItem("books")) || [];
    const found = books.find((b) => b.title === decodeURIComponent(title));
    setBook(found || null);
  }, [title]);

  const addToFavorites = () => {
    if (!book) return;
    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const exists = favorites.some((b) => b.title === book.title);

    if (!exists) {
      favorites.push(book);
      localStorage.setItem("favorites", JSON.stringify(favorites));
      window.dispatchEvent(new Event("favoritesUpdated"));
      alert("Added to favorites ‚ù§Ô∏è");
    } else {
      alert("Already in favorites");
    }
  };

  const addToCart = () => {
    if (!book) return;
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    const exists = cart.some((b) => b.title === book.title);

    if (!exists) {
      cart.push(book);
      localStorage.setItem("cart", JSON.stringify(cart));
      window.dispatchEvent(new Event("cartUpdated"));
      alert("Added to cart üõí");
    } else {
      alert("Already in cart");
    }
  };

  if (!book) {
    return (
      <div className="book-detail-empty">
        <h2>Book not found</h2>
        <button onClick={() => navigate("/")} className="btn back-btn">
          Go Back
        </button>
      </div>
    );
  }

  return (
    <main className="book-detail">
      <div className="book-detail-container">
        <img
          src={book.image || "/no-cover.png"}
          alt={book.title}
          className="book-detail-image"
        />

        <div className="book-detail-info">
          <h1 className="book-title">{book.title}</h1>
          <p className="book-author">{book.author || "Unknown author"}</p>
          <p className="book-price">{book.price ? `$${book.price}` : "Free"}</p>
          <p className="book-description">
            {book.description ||
              "No description available for this book at the moment."}
          </p>

          <div className="book-buttons">
            <button className="btn primary" onClick={addToCart}>
              Add to Cart
            </button>
            <button className="btn secondary" onClick={addToFavorites}>
              Add to Favorites
            </button>
            <button className="btn back-btn" onClick={() => navigate(-1)}>
              ‚Üê Back
            </button>
          </div>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
