import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './style.css';

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);

  useEffect(() => {
    // Ищем книгу в избранных или корзине
    const allBooks = [
      ...(JSON.parse(localStorage.getItem('ищщ')) || []),
      ...(JSON.parse(localStorage.getItem('cart')) || []),
    ];

    const found = allBooks.find((b) => b.title === title);
    setBook(found);
  }, [title]);

  if (!book) {
    return (
      <div className="book-detail">
        <p>Book not found</p>
        <button className="btn" onClick={() => navigate(-1)}>
          Go back
        </button>
      </div>
    );
  }

  return (
    <main className="book-detail">
      <button className="btn back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="book-detail__content">
        <img src={book.image} alt={book.title} className="book-detail__image" />

        <div className="book-detail__info">
          <h1>{book.title}</h1>
          <p className="author">by {book.author}</p>
          <p className="price">${book.price}</p>
          <p className="desc">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
            This is a placeholder description for the book detail page. 
            You can replace it with real data from the API later.
          </p>
          <button className="btn primary">Add to Bag</button>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
