import React, { useState, useEffect } from 'react';
import BookCard from '../../components/bookcard/BookCard';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Запрос в Open Library API (пример — fiction книги)
    fetch('https://openlibrary.org/search.json?q=fiction')
      .then(res => res.json())
      .then(data => {
        const results = data.docs.slice(0, 4).map(book => ({
          id: book.key,
          title: book.title,
          author: book.author_name ? book.author_name[0] : "Unknown Author",
          cover: book.cover_i
            ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
            : "https://via.placeholder.com/150x220?text=No+Cover",
          price: (Math.random() * (30 - 10) + 10).toFixed(2), // случайная цена $10–$30
          rating: Math.floor(Math.random() * 5) + 1 // рейтинг 1–5
        }));
        setBooks(results);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading books...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <main className="books-page">
      console.log("Books from API")
      <div className="books">
        {books.map(book => (
          <BookCard
            key={book.id}
            image={book.cover}
            rating={book.rating}
            title={book.title}
            author={book.author}
            price={book.price}
          />
        ))}
      </div>
    </main>
  );
};

export default Books;
