import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

const Books = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [priceRange, setPriceRange] = useState([0, 100]);
  const [rating, setRating] = useState(0);

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const query = params.get("search") || "all";

  useEffect(() => {
    setLoading(true);
    setError(null);

    fetch(`https://openlibrary.org/search.json?q=${query}`)
      .then((res) => res.json())
      .then((data) => {
        const bookList = data.docs.slice(0, 50).map((book) => ({
          key: book.key,
          title: book.title,
          author: book.author_name ? book.author_name[0] : "Unknown Author",
          image: book.cover_i
            ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
            : "https://via.placeholder.com/150x220?text=No+Cover",
          rating: Math.floor(Math.random() * 5) + 1,
          price: Math.floor(Math.random() * 50) + 10,
        }));
        setBooks(bookList);
        setFilteredBooks(bookList);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [query]);

  useEffect(() => {
    const filtered = books.filter(
      (book) =>
        book.price >= priceRange[0] &&
        book.price <= priceRange[1] &&
        book.rating >= rating
    );
    setFilteredBooks(filtered);
  }, [books, priceRange, rating]);

  return (
    <main className="books-container">
      {/* –§–∏–ª—å—Ç—Ä—ã —Å–ª–µ–≤–∞ */}
      <aside className="filters">
        <h2>üîç Filters</h2>

        <div className="filter-section">
          <label>üí∞ Price Range</label>
          <input
            type="range"
            min="0"
            max="100"
            value={priceRange[1]}
            onChange={(e) => setPriceRange([0, parseInt(e.target.value)])}
          />
          <p>${priceRange[0]} - ${priceRange[1]}</p>
        </div>

        <div className="filter-section">
          <label>‚≠ê Minimum Rating</label>
          <select
            value={rating}
            onChange={(e) => setRating(parseInt(e.target.value))}
          >
            <option value={0}>All</option>
            <option value={3}>3 stars & up</option>
            <option value={4}>4 stars & up</option>
            <option value={5}>5 stars</option>
          </select>
        </div>

        <button
          className="btn reset-btn"
          onClick={() => {
            setPriceRange([0, 100]);
            setRating(0);
            setFilteredBooks(books);
          }}
        >
          Reset Filters
        </button>
      </aside>

      {/* –ö–Ω–∏–≥–∏ —Å–ø—Ä–∞–≤–∞ */}
      <section className="books-grid">
        {loading ? (
          <div className="loader"></div>
        ) : error ? (
          <p className="error">Error: {error}</p>
        ) : filteredBooks.length === 0 ? (
          <p className="no-results">No books match your filters.</p>
        ) : (
          filteredBooks.map((book) => (
            <BookCard
              key={book.key}
              image={book.image}
              title={book.title}
              author={book.author}
              rating={book.rating}
              price={book.price}
            />
          ))
        )}
      </section>
    </main>
  );
};

export default Books;




