import React, { useEffect } from "react";
import { useBooks } from "../../context/BooksContext";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

const Fiction = () => {
  const { books, loading, error, fetchBooks } = useBooks();

  // Загружаем книги жанра Fiction при монтировании
  useEffect(() => {
    fetchBooks("subject:fiction");
  }, []);

  // Фильтрация только Fiction (если API возвращает больше)
  const fictionBooks = books.filter((book) =>
    (book.subject || []).includes("Fiction")
  );

  return (
    <main className="fiction-page">
      <h1 className="fiction-title">Fiction Books</h1>

      {loading ? (
        <div className="loader"></div>
      ) : error ? (
        <p className="error">Error: {error}</p>
      ) : (
        <section className="fiction-grid">
          {fictionBooks.slice(0, 20).map((book) => (
            <BookCard
              key={book.key}
              image={
                book.cover_i
                  ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
                  : "https://via.placeholder.com/150x220?text=No+Cover"
              }
              rating={Math.floor(Math.random() * 5) + 1}
              title={book.title}
              author={book.author_name ? book.author_name[0] : "Unknown Author"}
              price={Math.floor(Math.random() * 30) + 10}
            />
          ))}
        </section>
      )}
    </main>
  );
};

export default Fiction;
