import React, { useEffect, useState } from "react";
import { useBooks } from "../../context/BooksContext";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

const Fiction = () => {
  const { books, loading, error, fetchBySubject } = useBooks();

  const [page, setPage] = useState(1);
  const limit = 10; // Кол-во книг на страницу, можно изменить

  useEffect(() => {
    fetchBySubject("fiction", { page, limit });
  }, [page]); // обновляем данные при смене страницы

  const handlePrev = () => {
    if (page > 1) setPage(page - 1);
  };

  const handleNext = () => {
    // Для простоты, пока не знаем общее кол-во страниц, разрешаем листать вперёд всегда
    setPage(page + 1);
  };

  return (
    <main className="fiction-page">
      <h1 className="fiction-title">Fiction Books</h1>

      {loading ? (
        <div className="loader"></div>
      ) : error ? (
        <p className="error">Error: {error}</p>
      ) : (
        <>
          <section className="fiction-grid">
            {books.length > 0 ? (
              books.map((book) => (
                <BookCard
                  key={book.key}
                  image={
                    book.cover_id
                      ? `https://covers.openlibrary.org/b/id/${book.cover_id}-M.jpg`
                      : "https://via.placeholder.com/150x220?text=No+Cover"
                  }
                  rating={Math.floor(Math.random() * 5) + 1}
                  title={book.title}
                  author={book.authors ? book.authors[0].name : "Unknown Author"}
                  price={Math.floor(Math.random() * 30) + 10}
                />
              ))
            ) : (
              <p className="error">No books found</p>
            )}
          </section>

          <div className="pagination">
            <button onClick={handlePrev} disabled={page === 1}>
              ← Prev
            </button>
            <span>Page {page}</span>
            <button onClick={handleNext}>Next →</button>
          </div>
        </>
      )}
    </main>
  );
};

export default Fiction;
