import React, { useState, useEffect } from 'react';
import { useLocation } from "react-router-dom";
import BookCard from '../../components/bookcard/BookCard';
import './style.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [currentPage, setCurrentPage] = useState(1);
  const booksPerPage = 20;

  const [sortBy, setSortBy] = useState('default');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [ratingFilter, setRatingFilter] = useState('');

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const query = params.get("search") || "all";

  useEffect(() => {
    setLoading(true);
    setError(null);

    fetch(`https://openlibrary.org/search.json?q=${query}`)
      .then((res) => res.json())
      .then((data) => {
        setBooks(data.docs);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [query]);

  // Pagination logic
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;

  // Apply filters
  let filteredBooks = books.map((book) => ({
    key: book.key,
    image: book.cover_i
      ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
      : "https://via.placeholder.com/150x220?text=No+Cover",
    rating: Math.floor(Math.random() * 5) + 1,
    title: book.title,
    author: book.author_name ? book.author_name[0] : "Unknown Author",
    price: Math.floor(Math.random() * 30) + 10,
  }));

  if (minPrice) filteredBooks = filteredBooks.filter(b => b.price >= minPrice);
  if (maxPrice) filteredBooks = filteredBooks.filter(b => b.price <= maxPrice);
  if (ratingFilter) filteredBooks = filteredBooks.filter(b => b.rating >= ratingFilter);

  // Sorting
  if (sortBy === 'priceAsc') filteredBooks.sort((a, b) => a.price - b.price);
  if (sortBy === 'priceDesc') filteredBooks.sort((a, b) => b.price - a.price);
  if (sortBy === 'rating') filteredBooks.sort((a, b) => b.rating - a.rating);

  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  return (
    <main className="books-page">
      <div className="filters-container">
        <h2>📚 Filters</h2>
        <div className="filters-grid">
          <div>
            <label>Sort by:</label>
            <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
              <option value="default">Default</option>
              <option value="priceAsc">Price: Low to High</option>
              <option value="priceDesc">Price: High to Low</option>
              <option value="rating">Rating</option>
            </select>
          </div>

          <div>
            <label>Min Price ($)</label>
            <input
              type="number"
              value={minPrice}
              onChange={(e) => setMinPrice(e.target.value)}
              placeholder="10"
            />
          </div>

          <div>
            <label>Max Price ($)</label>
            <input
              type="number"
              value={maxPrice}
              onChange={(e) => setMaxPrice(e.target.value)}
              placeholder="50"
            />
          </div>

          <div>
            <label>Min Rating</label>
            <select
              value={ratingFilter}
              onChange={(e) => setRatingFilter(e.target.value)}
            >
              <option value="">All</option>
              <option value="1">1★ & up</option>
              <option value="2">2★ & up</option>
              <option value="3">3★ & up</option>
              <option value="4">4★ & up</option>
              <option value="5">5★ only</option>
            </select>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="loader"></div>
      ) : error ? (
        <p className="error">Error: {error}</p>
      ) : (
        <>
          <section className="books-grid">
            {currentBooks.map((book) => (
              <BookCard
                key={book.key}
                image={book.image}
                rating={book.rating}
                title={book.title}
                author={book.author}
                price={book.price}
              />
            ))}
          </section>

          <div className="pagination">
            {Array.from({ length: Math.ceil(filteredBooks.length / booksPerPage) }).map((_, index) => (
              <button
                key={index}
                className={`page-btn ${currentPage === index + 1 ? 'active' : ''}`}
                onClick={() => setCurrentPage(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </>
      )}
    </main>
  );
};

export default Books;




