import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './style.css';

import starIcon from './../../img/icons/star.svg';
import likeIcon from './../../img/icons/like.svg';
import likeFilledIcon from './../../img/icons/like-filled.svg';

import BookCard from './../../components/bookcard/BookCard'; // подключаем карточку

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [related, setRelated] = useState([]);
  const [loading, setLoading] = useState(true);
  const [liked, setLiked] = useState(false);
  const [inCart, setInCart] = useState(false);

  useEffect(() => {
    const allBooks = JSON.parse(localStorage.getItem('allBooks')) || [];
    const foundBook = allBooks.find((b) => b.title === title);

    if (foundBook) {
      const price = Math.floor(Math.random() * 40) + 10;
      const rating = Math.floor(Math.random() * 5) + 1;

      const formattedBook = {
        title: foundBook.title,
        author: foundBook.author_name
          ? foundBook.author_name.join(', ')
          : 'Unknown',
        cover: foundBook.cover_i
          ? `https://covers.openlibrary.org/b/id/${foundBook.cover_i}-L.jpg`
          : 'https://via.placeholder.com/150x220?text=No+Cover',
        first_publish_year: foundBook.first_publish_year,
        price,
        rating,
      };

      setBook(formattedBook);

      // Проверяем, есть ли книга в избранном и корзине
      const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
      const cart = JSON.parse(localStorage.getItem('cart')) || [];
      setLiked(favorites.some((f) => f.title === foundBook.title));
      setInCart(cart.some((c) => c.title === foundBook.title));

      // Выбираем 6 случайных книг
      const randomBooks = allBooks
        .filter((b) => b.title !== foundBook.title)
        .sort(() => 0.5 - Math.random())
        .slice(0, 6);
      setRelated(randomBooks);
    } else {
      setBook(null);
    }

    setLoading(false);
  }, [title]);

  const handleAddToFavorites = () => {
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    let updated;

    if (liked) {
      updated = favorites.filter((b) => b.title !== book.title);
      setLiked(false);
    } else {
      updated = [...favorites, book];
      setLiked(true);
    }

    localStorage.setItem('favorites', JSON.stringify(updated));
    window.dispatchEvent(new Event('favoritesUpdated'));
  };

  const handleAddToCart = () => {
    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    let updated;

    if (inCart) {
      updated = cart.filter((b) => b.title !== book.title);
      setInCart(false);
    } else {
      updated = [...cart, book];
      setInCart(true);
    }

    localStorage.setItem('cart', JSON.stringify(updated));
    window.dispatchEvent(new Event('cartUpdated'));
  };

  if (loading) return <p className="loading">Loading...</p>;

  if (!book)
    return (
      <div className="book-detail__empty">
        <p>Book not found</p>
        <button className="btn" onClick={() => navigate(-1)}>
          Go back
        </button>
      </div>
    );

  const stars = Array.from({ length: 5 }, (_, index) => (
    <img
      key={index}
      src={starIcon}
      alt="star"
      className={`book-detail__star ${index < book.rating ? 'filled' : ''}`}
    />
  ));

  return (
    <main className="book-detail">
      <button className="btn back-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <div className="book-detail__wrapper">
        {book.cover && (
          <img
            src={book.cover}
            alt={book.title}
            className="book-detail__image"
          />
        )}

        <div className="book-detail__info">
          <h2 className="book-detail__title">{book.title}</h2>
          <p className="book-detail__author">{book.author}</p>

          <div className="book-detail__rating">{stars}</div>
          <p className="book-detail__price">${book.price}</p>

          <p className="book-detail__desc">
            Discover the story of <strong>{book.title}</strong> — a captivating
            book that will stay with you long after you finish reading.
          </p>

          <div className="book-detail__actions">
            <button
              className={`book-detail__btn ${inCart ? 'in-cart' : ''}`}
              onClick={handleAddToCart}
            >
              {inCart ? 'Added to bag' : 'Add to bag'}
            </button>

            <img
              src={liked ? likeFilledIcon : likeIcon}
              alt="like"
              className="book-detail__like"
              onClick={handleAddToFavorites}
            />
          </div>
        </div>
      </div>

      {/* Рандомные книги */}
      {related.length > 0 && (
        <section className="related-books-section">
          <h3 className="related-title">You may also like</h3>
          <div className="related-books">
            {related.map((b, i) => (
              <BookCard
                key={i}
                image={
                  b.cover_i
                    ? `https://covers.openlibrary.org/b/id/${b.cover_i}-M.jpg`
                    : 'https://via.placeholder.com/150x220?text=No+Cover'
                }
                rating={Math.floor(Math.random() * 5) + 1}
                title={b.title}
                author={
                  b.author_name ? b.author_name[0] : 'Unknown Author'
                }
                price={Math.floor(Math.random() * 30) + 10}
              />
            ))}
          </div>
        </section>
      )}
    </main>
  );
};

export default BookDetail;
