import { useNavigate } from 'react-router-dom';
import BookCard from '../../components/bookcard/BookCard';
import Genre from '../../components/genre/Genre';
import Author from '../../components/author/Author';


import './style.css';

import sliderImage from './../../img/pictures/home/slide.png';
import bannerImage from './../../img/pictures/home/banner.png'; 

import book1 from './../../img/pictures/books/book1.png'; 
import book2 from './../../img/pictures/books/book2.png';
import book3 from './../../img/pictures/books/book3.png';
import book4 from './../../img/pictures/books/book4.png';
import book5 from './../../img/pictures/books/book5.png';
import book6 from './../../img/pictures/books/book6.png';
import book7 from './../../img/pictures/books/book7.png';

import genre1 from './../../img/pictures/genres/genre1.png';
import genre2 from './../../img/pictures/genres/genre2.png';
import genre3 from './../../img/pictures/genres/genre3.png';
import genre4 from './../../img/pictures/genres/genre4.png';

import author1 from './../../img/pictures/authors/author1.jpg';
import author2 from './../../img/pictures/authors/author2.jpg';
import author3 from './../../img/pictures/authors/author3.webp';
import author4 from './../../img/pictures/authors/author4.jpg';
import author5 from './../../img/pictures/authors/author5.avif';
import author6 from './../../img/pictures/authors/author6.jpg';
import author7 from './../../img/pictures/authors/author7.jpg';
import author8 from './../../img/pictures/authors/author8.jpg';

const Home = () => {
    const navigate = useNavigate();
    const handleClick = () => {navigate('/books');};

    return (
        <main className='home'>
            <section className='slider'>
                <img src={sliderImage} alt='Book Store Slider' className='slider_image' />

                <div className='slider_text'>
                    <h1>Welcome to <span>BookStore</span></h1>
                    <p>A new adventure starts with a single page</p>
                    <button className='btn_slider' onClick={handleClick}>Shop now</button>
                </div>
            </section>

            <section className="new-arrivals">
                <p className='arrivals_text1'>New Arrivals</p>
                <hr className='line1' />
                <p className='arrivals_text2'>Fresh stories, just for you!</p>
                {/* <button className="view-all">view all</button> */}
            </section>

            <section className='books'>
                <BookCard
                    image={book1}
                    rating={5}
                    title="Sunrise on the Reaping"
                    author="Suzanne Collins"
                    price={20}
                />

                <BookCard
                    image={book2}
                    rating={4}
                    title="Dream Count"
                    author="Chimamanda Ngozi Adichie"
                    price={20}
                />

                <BookCard
                    image={book3}
                    rating={3}
                    title="Three Days in June"
                    author="Anne Tyler"
                    price={20}
                />

                <BookCard
                    image={book4}
                    rating={5}
                    title="Great Big Beautiful Life"
                    author="Emily Henry"
                    price={20}
                />
                <BookCard
                    image={book5}
                    rating={5}
                    title="Ashley Winstead"
                    author="This Book Will Bury Me"
                    price={20}
                />
            </section>

            <section className='banner'>
                <img src={bannerImage} alt='Book Store Slider' className='banner_image' />

                <div className='banner_items'>
                    <div className='banner_text'>
                        <h1>Bestsellers of the Month</h1>
                        <p>The books that flew off the shelves – and into readers’ hearts!</p>
                        <button className='btn_banner' onClick={handleClick}>Shop now</button>
                    </div>

                    <div className='banner_books'>
                        <img src={book4} alt='Book Cover' className='banner_book' />
                        <img src={book6} alt='Book Cover' className='banner_book' />
                        <img src={book7} alt='Book Cover' className='banner_book' />
                        <img src={book5} alt='Book Cover' className='banner_book' />
                    </div>
                </div>
            </section>

            <section className="genres_text">
                <h1>Genres Books</h1>
                <hr className='line2' />
                <p>Find the stories that match your mood!</p>
            </section>

            <section className='genres'>
                <Genre
                    image={genre1}
                    title="Sci-fi"
                />
                <Genre
                    image={genre2}
                    title="Fantasy"
                />
                <Genre
                    image={genre3}
                    title="Love Story"
                />
                <Genre
                    image={genre4}
                    title="Adventure"
                />
            </section>

            <section className="authors">
                <h1>Popular authors</h1>
            </section>

            <section className='authors_grid'>
                    <Author
                        image={author1}
                        title="Stephen King"
                    />
                    <Author
                        image={author2}
                        title="J.K. Rowling"
                    />
                    <Author
                        image={author3}
                        title="Haruki Murakami"
                    />
                    <Author
                        image={author4}
                        title="Gabriel García Márquez"
                    />
                    <Author
                        image={author5}
                        title="Agatha Christie"
                    />
                    <Author
                        image={author6}
                        title="George R.R. Martin"
                    />
                    <Author
                        image={author7}
                        title="Isabel Allende"
                    />
                    <Author
                        image={author8}
                        title="Neil Gaiman"
                    />
                </section>
        </main>
    );
};

export default Home;

