import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './style.css';

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [addedToFavorites, setAddedToFavorites] = useState(false);
  const [addedToCart, setAddedToCart] = useState(false);

  useEffect(() => {
    const fetchBook = async () => {
      setLoading(true);
      try {
        const res = await fetch(`https://openlibrary.org/search.json?title=${encodeURIComponent(title)}`);
        const data = await res.json();

        if (data.docs && data.docs.length > 0) {
          const b = data.docs[0];
          const price = Math.floor(Math.random() * 40) + 10;
          const rating = Math.floor(Math.random() * 5) + 1;

          setBook({
            title: b.title,
            author: b.author_name ? b.author_name.join(', ') : 'Unknown',
            cover: b.cover_i ? `https://covers.openlibrary.org/b/id/${b.cover_i}-L.jpg` : null,
            first_publish_year: b.first_publish_year,
            price,
            rating
          });
        } else {
          setBook(null);
        }
      } catch (error) {
        console.error('Error fetching book:', error);
        setBook(null);
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [title]);

  const handleAddToFavorites = () => {
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    if (!favorites.some((b) => b.title === book.title)) {
      favorites.push(book);
      localStorage.setItem('favorites', JSON.stringify(favorites));
      setAddedToFavorites(true);
      window.dispatchEvent(new Event('favoritesUpdated'));
    } else {
      alert('This book is already in Favorites!');
    }
  };

  const handleAddToCart = () => {
    const cart = JSON.parse(localStorage.getItem('cart')) || [];
    if (!cart.some((b) => b.title === book.title)) {
      cart.push(book);
      localStorage.setItem('cart', JSON.stringify(cart));
      setAddedToCart(true);
      window.dispatchEvent(new Event('cartUpdated'));
    } else {
      alert('This book is already in Cart!');
    }
  };

  if (loading) return <p className="loading">Loading...</p>;

  if (!book) {
    return (
      <div className="book-detail">
        <p>Book not found</p>
        <button className="btn" onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  return (
    <main className="book-detail">
      <button className="btn back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="book-detail__card">
        {book.cover && <img src={book.cover} alt={book.title} className="book-detail__image" />}

        <div className="book-detail__info">
          <h1>{book.title}</h1>
          <p className="author">by {book.author}</p>
          {book.first_publish_year && <p className="year">First published: {book.first_publish_year}</p>}

          <p className="price">${book.price}</p>

          <div className="rating">
            {'⭐'.repeat(book.rating)} <span>({book.rating}/5)</span>
          </div>

          <p className="desc">
            A captivating book that explores timeless ideas and stories.  
            Dive into a world of imagination, emotion, and inspiration.
          </p>

          <div className="btn-group">
            <button 
              className={`btn primary ${addedToCart ? 'disabled' : ''}`}
              onClick={handleAddToCart}
              disabled={addedToCart}
            >
              {addedToCart ? '✔ Added to Cart' : '🛒 Add to Cart'}
            </button>

            <button 
              className={`btn favorite ${addedToFavorites ? 'disabled' : ''}`}
              onClick={handleAddToFavorites}
              disabled={addedToFavorites}
            >
              {addedToFavorites ? '❤️ In Favorites' : '♡ Add to Favorites'}
            </button>
          </div>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
