import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './style.css';

const BookDetail = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBook = async () => {
      setLoading(true);
      try {
        const res = await fetch(`https://openlibrary.org/search.json?title=${encodeURIComponent(title)}`);
        const data = await res.json();
        if (data.docs && data.docs.length > 0) {
          const b = data.docs[0]; // Берём первую книгу
          setBook({
            title: b.title,
            author: b.author_name ? b.author_name.join(', ') : 'Unknown',
            cover: b.cover_i ? `https://covers.openlibrary.org/b/id/${b.cover_i}-L.jpg` : null,
            first_publish_year: b.first_publish_year,
          });
        } else {
          setBook(null);
        }
      } catch (error) {
        console.error('Error fetching book:', error);
        setBook(null);
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [title]);

  if (loading) return <p>Loading...</p>;

  if (!book) {
    return (
      <div className="book-detail">
        <p>Book not found</p>
        <button className="btn" onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  return (
    <main className="book-detail">
      <button className="btn back-btn" onClick={() => navigate(-1)}>← Back</button>

      <div className="book-detail__content">
        {book.cover && <img src={book.cover} alt={book.title} className="book-detail__image" />}

        <div className="book-detail__info">
          <h1>{book.title}</h1>
          <p className="author">by {book.author}</p>
          {book.first_publish_year && <p className="year">First published: {book.first_publish_year}</p>}
          <p className="desc">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
            This is a placeholder description for the book detail page. 
            You can replace it with real data from the API later.
          </p>
          <button className="btn primary">Add to Bag</button>
        </div>
      </div>
    </main>
  );
};

export default BookDetail;
