import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useBooks } from "../../context/BooksContext";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

const Books = () => {
  const { books, loading, error, query, setQuery, page, setPage, limit, setLimit } = useBooks();
  const location = useLocation();
  const navigate = useNavigate();

  // Парсим параметры из URL
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const searchParam = params.get("search") || "all";
    const pageParam = parseInt(params.get("page"), 10) || 1;
    const limitParam = parseInt(params.get("limit"), 10) || 10;

    // Обновляем состояние контекста при изменении URL
    if (searchParam !== query) setQuery(searchParam);
    if (pageParam !== page) setPage(pageParam);
    if (limitParam !== limit) setLimit(limitParam);
  }, [location.search]);

  // Обновляем URL при изменении параметров через UI (например, выбор лимита или пагинация)
  const updateParams = (newParams) => {
    const params = new URLSearchParams(location.search);
    Object.entries(newParams).forEach(([key, value]) => {
      if (value === undefined || value === null) {
        params.delete(key);
      } else {
        params.set(key, value);
      }
    });
    navigate(`/books?${params.toString()}`, { replace: false });
  };

  return (
    <main className="books-page">
      <h2>Results for: {query}</h2>

      <div className="limit-select">
        <span>Show: </span>
        <select
          value={limit}
          onChange={(e) => updateParams({ limit: e.target.value, page: 1 })}
        >
          <option value="5">5</option>
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="30">30</option>
        </select>
      </div>

      {loading ? (
        <div className="loader"></div>
      ) : error ? (
        <p className="error">Error: {error}</p>
      ) : (
        <section className="books-grid">
          {books.map((book) => (
            <BookCard
              key={book.key}
              image={
                book.cover_i
                  ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
                  : "https://via.placeholder.com/150x220?text=No+Cover"
              }
              rating={Math.floor(Math.random() * 5) + 1}
              title={book.title}
              author={book.author_name ? book.author_name[0] : "Unknown Author"}
              price={Math.floor(Math.random() * 30) + 10}
            />
          ))}
        </section>
      )}

      <div className="pagination">
        <button disabled={page <= 1} onClick={() => updateParams({ page: page - 1 })}>
          ←
        </button>
        <span>Page {page}</span>
        <button onClick={() => updateParams({ page: page + 1 })}>→</button>
      </div>

      {books.length === 0 && !loading && (
        <p className="no-books">No books found for "{query}".</p>
      )}
    </main>
  );
};

export default Books;
