import React, { useState, useEffect } from 'react';
import BookCard from '../../components/bookcard/BookCard';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState("fiction"); // дефолтный запрос
  const [searchInput, setSearchInput] = useState("");
  const [searchType, setSearchType] = useState("title"); // "title" или "author"

  const fetchBooks = (searchQuery, type = "title") => {
    setLoading(true);
    setError(null);

    const url =
      type === "author"
        ? `https://openlibrary.org/search.json?author=${searchQuery}`
        : `https://openlibrary.org/search.json?title=${searchQuery}`;

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        const results = data.docs.slice(0, 8).map((book) => ({
          id: book.key,
          title: book.title,
          author: book.author_name ? book.author_name[0] : "Unknown Author",
          cover: book.cover_i
            ? `https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`
            : "https://via.placeholder.com/150x220?text=No+Cover",
          price: (Math.random() * (30 - 10) + 10).toFixed(2), // случайная цена $10–$30
          rating: Math.floor(Math.random() * 5) + 1, // рейтинг 1–5
        }));
        setBooks(results);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchBooks(query, searchType);
  }, [query, searchType]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchInput.trim() !== "") {
      setQuery(searchInput.trim());
    }
  };

  return (
    <main className="books-page">
      {/* Поисковая форма */}
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          placeholder={`Search by ${searchType}...`}
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
          className="search-input"
        />
        <select
          value={searchType}
          onChange={(e) => setSearchType(e.target.value)}
          className="search-select"
        >
          <option value="title">Title</option>
          <option value="author">Author</option>
        </select>
        <button type="submit" className="search-btn">
          Search
        </button>
      </form>

      {/* Загрузка/ошибки */}
      {loading && <p className="loading">Loading books...</p>}
      {error && <p className="error">Error: {error}</p>}

      {/* Книги */}
      <div className="books">
        {books.map((book) => (
          <BookCard
            key={book.id}
            image={book.cover}
            rating={book.rating}
            title={book.title}
            author={book.author}
            price={book.price}
          />
        ))}
      </div>
    </main>
  );
};

export default Books;
