import React, { useEffect, useState } from "react";
import { onAuthStateChanged, signOut, updateProfile } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { auth } from "../../firebase";
import "./style.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const [newName, setNewName] = useState("");
  const [activeTab, setActiveTab] = useState("account");
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        setNewName(currentUser.displayName || "");
      } else {
        navigate("/login");
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  const handleLogout = async () => {
    await signOut(auth);
    navigate("/login");
  };

  const handleSaveName = async () => {
    if (!newName.trim()) return;
    try {
      await updateProfile(auth.currentUser, { displayName: newName });
      setUser({ ...auth.currentUser, displayName: newName });
      setEditing(false);
    } catch (err) {
      console.error("Error updating name:", err);
      alert("Failed to update name");
    }
  };

  if (!user) return null;

  return (
    <main className="profile-page">
      <div className="profile-container">
        <aside className="profile-sidebar">
          <img
            src={`https://ui-avatars.com/api/?name=${encodeURIComponent(
              user.displayName || user.email
            )}&background=ffc300&color=000`}
            alt="avatar"
            className="profile-avatar"
          />
          <h3>{user.displayName || user.email.split("@")[0]}</h3>
          <p>{user.email}</p>

          <nav className="profile-menu">
            <button
              className={activeTab === "account" ? "active" : ""}
              onClick={() => setActiveTab("account")}
            >
              üë§ My Account
            </button>
            <button
              className={activeTab === "favorites" ? "active" : ""}
              onClick={() => navigate("/favorites")}
            >
              ‚ù§Ô∏è Favorites
            </button>
            <button
              className={activeTab === "orders" ? "active" : ""}
              onClick={() => setActiveTab("orders")}
            >
               My Orders
            </button>
          </nav>

          <button className="logout-btn" onClick={handleLogout}>
            Log out
          </button>
        </aside>

        <section className="profile-content">
          {activeTab === "account" && (
            <div className="tab-content">
              <h2>Account Details</h2>
              <div className="profile-info">
                <label>Username</label>
                {editing ? (
                  <div className="edit-row">
                    <input
                      type="text"
                      value={newName}
                      onChange={(e) => setNewName(e.target.value)}
                      className="edit-input"
                    />
                    <button className="save-btn" onClick={handleSaveName}>
                      Save
                    </button>
                  </div>
                ) : (
                  <div className="info-row">
                    <span>{user.displayName || "Not set"}</span>
                    <button
                      className="edit-btn"
                      onClick={() => setEditing(true)}
                    >
                      Edit
                    </button>
                  </div>
                )}

                <label>Email</label>
                <p>{user.email}</p>

                <label>Joined</label>
                <p>{user.metadata.creationTime}</p>
              </div>
            </div>
          )}

          {activeTab === "orders" && (
            <div className="tab-content">
              <h2>My Orders</h2>
              <p>You haven‚Äôt placed any orders yet.</p>
            </div>
          )}

          {activeTab === "settings" && (
            <div className="tab-content">
              <h2>Settings</h2>
              <p>Coming soon ‚Äî notifications, preferences, and theme.</p>
            </div>
          )}
        </section>
      </div>
    </main>
  );
};

export default Profile;
