import { createContext, useContext, useState, useEffect } from "react";

const BooksContext = createContext();

export const BooksProvider = ({ children }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBooks = (query = "all") => {
    setLoading(true);
    setError(null);

    fetch(`https://openlibrary.org/search.json?q=${query}`)
      .then((res) => res.json())
      .then((data) => {
        setBooks(data.docs || []);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  };

  const fetchBySubject = (subject) => {
    setLoading(true);
    setError(null);

    fetch(`https://openlibrary.org/subjects/${subject}.json?limit=20`)
      .then((res) => res.json())
      .then((data) => {
        setBooks(data.works || []);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  };

  const fetchBookDetail = async (title) => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`https://openlibrary.org/search.json?title=${encodeURIComponent(title)}`);
      const data = await res.json();
      return data.docs?.[0] || null;
    } catch (err) {
      setError(err.message);
      return null;
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks("all");
  }, []);

  return (
    <BooksContext.Provider value={{ books, loading, error, fetchBooks, fetchBySubject }}>
      {children}
    </BooksContext.Provider>
  );
};

export const useBooks = () => useContext(BooksContext);
