import React, { useEffect, useState } from 'react';
import BookCard from '../../components/bookcard/BookCard';
import './style.css';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [selected, setSelected] = useState([]);

  // Загружаем избранные книги из localStorage
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavorites(saved);
  }, []);

  // Сохраняем изменения в localStorage
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  // Удалить одну книгу
  const removeFavorite = (title) => {
    const updated = favorites.filter((book) => book.title !== title);
    setFavorites(updated);
  };

  // Удалить выбранные книги
  const removeSelected = () => {
    const updated = favorites.filter((book) => !selected.includes(book.title));
    setFavorites(updated);
    setSelected([]);
  };

  // Удалить все
  const clearAll = () => {
    setFavorites([]);
    setSelected([]);
    localStorage.removeItem('favorites');
  };

  // Выбрать / отменить выбор книги
  const toggleSelect = (title) => {
    setSelected((prev) =>
      prev.includes(title)
        ? prev.filter((t) => t !== title)
        : [...prev, title]
    );
  };

  return (
    <main className="favorites-page">
      <h1 className="favorites-title">💛 My Favorites</h1>

      {favorites.length === 0 ? (
        <p className="favorites-empty">No books added yet.</p>
      ) : (
        <>
          <div className="favorites-actions">
            {selected.length > 0 && (
              <button className="btn remove-selected" onClick={removeSelected}>
                Remove Selected ({selected.length})
              </button>
            )}
            <button className="btn clear-all" onClick={clearAll}>
              Clear All
            </button>
          </div>

          <div className="favorites-grid">
            {favorites.map((book) => (
              <div
                key={book.title}
                className={`favorite-item ${selected.includes(book.title) ? 'selected' : ''}`}
              >
                <BookCard
                  image={book.image}
                  title={book.title}
                  author={book.author}
                  price={book.price}
                  rating={book.rating}
                />
                <div className="favorite-buttons">
                  <button
                    className="btn small"
                    onClick={() => toggleSelect(book.title)}
                  >
                    {selected.includes(book.title) ? 'Unselect' : 'Select'}
                  </button>
                  <button
                    className="btn small remove"
                    onClick={() => removeFavorite(book.title)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </main>
  );
};

export default Favorites;
