import React from 'react';
import { useNavigate } from 'react-router-dom';
import './style.css';

const Genre = ({image, title}) => {
    const navigate = useNavigate();

    const genreQuery = title.toLowerCase().replace(/\s+/g, "+");

    const handleClick = () => {
        navigate(`/books?genre=${genreQuery}`);
    }

    return (
        <div className="genre" onClick={handleClick}>
            <img src={image} alt={title} className="genre__image" />
            <h4 className="genre__title">{title}</h4>
        </div>
    )
};

export default Genre;
