import React, { useEffect, useState } from 'react';
import './style.css';

const Favorites = () => {
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const saved = JSON.parse(localStorage.getItem('favorites')) || [];
        setFavorites(saved);

        const handleUpdate = () => {
            const updated = JSON.parse(localStorage.getItem('favorites')) || [];
            setFavorites(updated);
        };
        window.addEventListener('favoritesUpdated', handleUpdate);

        return () => window.removeEventListener('favoritesUpdated', handleUpdate);
    }, []);

    const removeFromFavorites = (title) => {
        const updated = favorites.filter((book) => book.title !== title);
        localStorage.setItem('favorites', JSON.stringify(updated));
        setFavorites(updated);
    };

    const clearFavorites = () => {
        localStorage.removeItem('favorites');
        setFavorites([]);
    };

    return (
        <main className="favorites-page">
            <h1 className="favorites-title">ðŸ’› My Favorites</h1>

            {favorites.length === 0 ? (
                <p className="favorites-empty">No books in favorites yet.</p>
            ) : (
                <>
                    <div className="favorites-grid">
                        {favorites.map((book, index) => (
                            <div key={index} className="favorites-item">
                                <img
                                    src={book.image}
                                    alt={book.title}
                                    className="favorites-image"
                                />
                                <div className="favorites-info">
                                    <h3>{book.title}</h3>
                                    <p>{book.author}</p>
                                    <p className="favorites-price">${book.price}</p>
                                </div>
                                <button
                                    className="btn remove"
                                    onClick={() => removeFromFavorites(book.title)}
                                >
                                    Remove
                                </button>
                            </div>
                        ))}
                    </div>

                    <button className="btn clear-all" onClick={clearFavorites}>
                        Clear all
                    </button>
                </>
            )}
        </main>
    );
};

export default Favorites;
