import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useBooks } from "../../context/BooksContext";
import BookCard from "../../components/bookcard/BookCard";
import "./style.css";

const BookDetail = () => {
  const { title } = useParams();
  const { fetchBySubject, books } = useBooks();

  const [book, setBook] = useState(null);
  const [relatedBooks, setRelatedBooks] = useState([]);
  const [expanded, setExpanded] = useState(false);
  const [genre, setGenre] = useState("fiction"); // üîπ –∂–∞–Ω—Ä –¥–ª—è –ø–æ–∏—Å–∫–∞

  // üîπ useEffect: –∑–∞–≥—Ä—É–∑–∫–∞ –∫–Ω–∏–≥ –ø–æ –∂–∞–Ω—Ä—É
  useEffect(() => {
    fetchBySubject(genre);
  }, [genre]); // –º–µ–Ω—è–µ–º –∫–Ω–∏–≥–∏ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –∂–∞–Ω—Ä–∞

  // üîπ useEffect: –ø–æ–∏—Å–∫ –Ω—É–∂–Ω–æ–π –∫–Ω–∏–≥–∏ –∏ –ø–æ–¥–±–æ—Ä –ø–æ—Ö–æ–∂–∏—Ö
  useEffect(() => {
    if (!books || books.length === 0) return;

    const found = books.find((b) => b.title === title);
    if (!found) return;

    const bookData = {
      ...found,
      price: found.price || Math.floor(Math.random() * 40) + 10,
      author:
        found.author_name && found.author_name.length > 0
          ? found.author_name.join(", ")
          : "Unknown Author",
      image: found.cover_i
        ? `https://covers.openlibrary.org/b/id/${found.cover_i}-L.jpg`
        : "https://via.placeholder.com/150x220?text=No+Cover",
    };

    setBook(bookData);

    const shuffled = [...books].sort(() => 0.5 - Math.random());
    setRelatedBooks(shuffled.slice(0, 5));
  }, [title, books]); // üîπ –ø—Ä–∏ —Å–º–µ–Ω–µ title —ç—Ñ—Ñ–µ–∫—Ç –∑–∞–ø—É—Å–∫–∞–µ—Ç—Å—è —Å–Ω–æ–≤–∞

  if (!book)
    return (
      <main className="book-detail">
        <p className="book-detail__empty">Book not found</p>
      </main>
    );

  return (
    <main className="book-detail">
      <h1 className="book-detail__title">{book.title}</h1>

      {/* üîπ –í—ã–±–æ—Ä –∂–∞–Ω—Ä–∞ */}
      <div className="genre-select">
        <label htmlFor="genre">Choose genre: </label>
        <select
          id="genre"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        >
          <option value="fiction">Fiction</option>
          <option value="fantasy">Fantasy</option>
          <option value="romance">Romance</option>
          <option value="mystery">Mystery</option>
          <option value="history">History</option>
        </select>
      </div>

      <div className="book-detail__main">
        <BookCard
          image={book.image}
          title={book.title}
          author={book.author}
          price={book.price}
          rating={book.rating || 4}
          showButtons={true}
        />

        <div className="book-detail__desc">
          <p>
            Discover the story of <strong>{book.title}</strong> ‚Äî a captivating
            book that will stay with you long after you finish reading.
          </p>
          <p>
            Written by <strong>{book.author}</strong>, this book explores
            powerful ideas and emotions that resonate with readers of all ages.
          </p>

          {/* üîπ –ö–Ω–æ–ø–∫–∞ Read More */}
          <button
            className="btn-readmore"
            onClick={() => setExpanded(!expanded)}
          >
            {expanded ? "Hide Description" : "Read More"}
          </button>

          {expanded && (
            <p className="book-detail__extra">
              This book dives deep into human emotions, creativity, and the
              beauty of storytelling. Perfect for readers who love immersive
              worlds and meaningful characters.
            </p>
          )}
        </div>
      </div>

      {relatedBooks.length > 0 && (
        <section className="related-books">
          <h3 className="related-books__title">You also may like</h3>
          <div className="books-grid">
            {relatedBooks.map((b, i) => (
              <BookCard
                key={i}
                image={
                  b.cover_i
                    ? `https://covers.openlibrary.org/b/id/${b.cover_i}-M.jpg`
                    : "https://via.placeholder.com/150x220?text=No+Cover"
                }
                rating={b.rating || Math.floor(Math.random() * 5) + 1}
                title={b.title}
                author={
                  b.author_name && b.author_name.length > 0
                    ? b.author_name[0]
                    : "Unknown Author"
                }
                price={b.price || Math.floor(Math.random() * 30) + 10}
                showButtons={true}
              />
            ))}
          </div>
        </section>
      )}
    </main>
  );
};

export default BookDetail;
