import React, { useState } from 'react';

import './style.css';

import starIcon from './../../img/icons/star.svg';
import likeIcon from './../../img/icons/like.svg';
import likeFilledIcon from './../../img/icons/like-filled.svg';

const BookCard = ({ image, title, author, price, rating, book }) => {
  const [isFavorite, setIsFavorite] = useState(false);

  // Проверяем, есть ли книга в избранных при загрузке
  useEffect(() => {
    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
    setIsFavorite(favorites.some((fav) => fav.key === book.key));
  }, [book.key]);

  // Добавление/удаление книги из избранного
  const toggleFavorite = () => {
    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
    let updatedFavorites;

    if (isFavorite) {
      updatedFavorites = favorites.filter((fav) => fav.key !== book.key);
    } else {
      updatedFavorites = [...favorites, book];
    }

    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
    setIsFavorite(!isFavorite);
  };

  return (
    <div className="book-card">
      <img className="book-card__image" src={image} alt={title} />

      <div className="book-card__info">
        <h3 className="book-card__title">{title}</h3>
        <p className="book-card__author">{author}</p>
        <p className="book-card__price">${price}</p>
      </div>

      <div className="book-card__actions">
        <button className="book-card__btn">Buy</button>
        <img
          src={isFavorite ? "/icons/heart-filled.svg" : "/icons/heart.svg"}
          alt="like"
          className="book-card__like"
          onClick={toggleFavorite}
        />
      </div>
    </div>
  );
};

export default BookCard;

