import React, { createContext, useContext, useState } from "react";

const BooksContext = createContext();

export const BooksProvider = ({ children }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchBooks = async (query = "all") => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`https://openlibrary.org/search.json?q=${encodeURIComponent(query)}&limit=40`);
      const data = await res.json();

      if (data.docs) {
        // ✅ Добавляем фиксированные price и rating при первой загрузке
        const processed = data.docs.map((book) => ({
          ...book,
          price: Math.floor(Math.random() * 40) + 10,
          rating: Math.floor(Math.random() * 5) + 1,
        }));

        setBooks(processed);
      }
    } catch (err) {
      console.error("Error fetching books:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <BooksContext.Provider value={{ books, loading, error, fetchBooks }}>
      {children}
    </BooksContext.Provider>
  );
};

export const useBooks = () => useContext(BooksContext);
