import React, { useEffect, useState } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { doc, setDoc, getDoc } from "firebase/firestore";
import { auth, db } from "../../firebase";
import "./style.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState("account");
  const [uploading, setUploading] = useState(false);
  const [photoURL, setPhotoURL] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);


        const docRef = doc(db, "users", currentUser.uid);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          const data = docSnap.data();
          if (data.photoBase64) {
            setPhotoURL(data.photoBase64);
          } else {
            setPhotoURL(currentUser.photoURL);
          }
        } else {
          setPhotoURL(currentUser.photoURL);
        }
      } else {
        // –ï—Å–ª–∏ –Ω–µ–∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω, —Ä–µ–¥–∏—Ä–µ–∫—Ç –Ω–∞ –ª–æ–≥–∏–Ω
        window.location.href = "/login";
      }
    });

    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    await signOut(auth);
    window.location.href = "/login";
  };

  const compressAndConvertToBase64 = (file) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onload = (event) => {
        const img = new Image();
        img.onload = () => {
          const canvas = document.createElement("canvas");
          const maxWidth = 300; // –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ —à–∏—Ä–∏–Ω—ã
          const scaleSize = maxWidth / img.width;
          canvas.width = maxWidth;
          canvas.height = img.height * scaleSize;

          const ctx = canvas.getContext("2d");
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

          // –ü–æ–ª—É—á–∞–µ–º —Å–∂–∞—Ç–æ–µ Base64 –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ (jpeg, –∫–∞—á–µ—Å—Ç–≤–æ 0.6)
          const compressedBase64 = canvas.toDataURL("image/jpeg", 0.6);
          resolve(compressedBase64);
        };
        img.src = event.target.result;
      };
      reader.readAsDataURL(file);
    });
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.match(/image\/(jpeg|png)/)) {
      alert("Only JPG/PNG images allowed!");
      return;
    }

    setUploading(true);

    try {
      const compressedBase64 = await compressAndConvertToBase64(file);

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ Firestore –ø–æ–¥ user.uid
      const userDocRef = doc(db, "users", user.uid);
      await setDoc(userDocRef, { photoBase64: compressedBase64 }, { merge: true });

      // –û–±–Ω–æ–≤–ª—è–µ–º –ª–æ–∫–∞–ª—å–Ω–æ –æ—Ç–æ–±—Ä–∞–∂–∞–µ–º–æ–µ —Ñ–æ—Ç–æ
      setPhotoURL(compressedBase64);

      // –ú–æ–∂–Ω–æ –æ–±–Ω–æ–≤–∏—Ç—å photoURL –≤ Firebase Auth (–Ω–æ –æ–±—ã—á–Ω–æ —ç—Ç–æ URL, –ø–æ—ç—Ç–æ–º—É –æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)
      // await updateProfile(user, { photoURL: compressedBase64 });

      alert("Profile picture updated!");
    } catch (error) {
      alert("Upload failed: " + error.message);
    }

    setUploading(false);
  };

  if (!user) return null;

  return (
    <main className="profile-page">
      <div className="profile-container">
        <aside className="profile-sidebar">
          <img
            src={
              photoURL ||
              `https://ui-avatars.com/api/?name=${encodeURIComponent(
                user.displayName || user.email
              )}&background=ffc300&color=000`
            }
            alt="avatar"
            className="profile-avatar"
          />
          <h3>{user.displayName || user.email.split("@")[0]}</h3>
          <p>{user.email}</p>

          <input
            type="file"
            accept=".jpg,.jpeg,.png"
            onChange={handleFileChange}
            disabled={uploading}
            style={{ marginTop: "10px" }}
          />

          <nav className="profile-menu">
            <button
              className={activeTab === "account" ? "active" : ""}
              onClick={() => setActiveTab("account")}
            >
              üë§ My Account
            </button>
            <button
              className={activeTab === "favorites" ? "active" : ""}
              onClick={() => (window.location.href = "/favorites")}
            >
              ‚ù§Ô∏è Favorites
            </button>
            <button
              className={activeTab === "orders" ? "active" : ""}
              onClick={() => (window.location.href = "/cart")}
            >
              üõí Cart
            </button>
          </nav>

          <button className="logout-btn" onClick={handleLogout}>
            Log out
          </button>
        </aside>

        <section className="profile-content">
          {activeTab === "account" && (
            <div className="tab-content">
              <h2>Account Details</h2>
              <div className="profile-info">
                <label>Username</label>
                <div className="info-row">
                  <span>{user.displayName || "Not set"}</span>
                </div>

                <label>Email</label>
                <p>{user.email}</p>

                <label>Joined</label>
                <p>{user.metadata.creationTime}</p>
              </div>
            </div>
          )}
        </section>
      </div>
    </main>
  );
};

export default Profile;
